using UnityEngine;using System.Collections;using UnityEngine.Networking;using UnityEngine.VR;public class CameraManager : NetworkBehaviour {    public GameObject topDownCamera;    public GameObject VRCamera;    public GameObject canvas;    bool setup = false;    void Start ()    {        VRSettings.enabled = false;    }	// Update is called once per frame	void Update () {        if (!setup)        {            if(isServer)            {                VRSettings.enabled = false;                VRCamera.SetActive(false);                topDownCamera.SetActive(true);            }            else if(isClient)            {                VRSettings.enabled = true;                VRCamera.SetActive(true);                topDownCamera.SetActive(false);                canvas.SetActive(false);            }        }    }}